    // | R | | R |   | R | | R |     | R | | R |    | R | | R |         limit amount, and any other trading information.
    // | D | | D |   | D | | D |     | D | | D |    | D | | D |
    // | E | | E |   | E | | E |     | E | | E |    | E | | E |
    // | R | | R |   | R | | R |     | R | | R |    | R | | R |
    // |___| |___|   |___| |___|     |___| |___|    |___| |___|





    /* ---------------------------------------------------------------------- */
    // Contract Settings
    /* ---------------------------------------------------------------------- */
    function changeRouter(IUniswapV2Router02 _router) external;
    function addVault(address _vault) external;
    function activateVault(uint256 _vaultId) external;
