    function setMinimumDeposit(uint256 _vaultId, uint256 _minimumDeposit) external;
    function setLendingPool(IAaveStake _lendingPool) external;
    function setRewards(IAaveStake _rewards) external;
    function addAaveMarketTokens(address[] calldata _amTokens) external;
    function removeAaveMarketToken(address _amToken) external;
    function feePoints() external view returns (uint256);
    function feeBasePoints() external view returns (uint256);



